import { put, takeEvery, all } from "redux-saga/effects";
import axios from "axios";
import * as types from '../actions/ActionTypes';
import * as postActions from '../actions/post';
import * as courseActions from '../actions/course';

function* getCourses(action) {
    const { data } = yield axios.get("http://localhost:7070/api/courses");
    console.log('getCourses');
    try {
        console.log('getCourses try', action);
        // action.posts.forEach((post)=>{
        //     data.forEach((course)=>{
        //         if(post.course_id == course.id)
        //             post.course_title = course.title
        //     });
        // });

        // console.log(posts);

        // yield put(postActions.getPostsSuccess(posts));
        yield put(courseActions.getCourseSuccess(data));

    } catch (error) {
    }
}

export default function* root() {
    yield all([
        takeEvery(types.GET_COURSES, getCourses)
    ]);
}
