import { put, takeEvery, all } from "redux-saga/effects";
import axios from "axios";
import * as types from '../actions/ActionTypes';
import * as postActions from '../actions/post';
import * as courseActions from '../actions/course';

function* getPosts() {
    // const { post } = yield  axios.get("http://localhost:7070/api/posts");
    // const { course } = yield axios.get("http://localhost:7070/api/courses");
    const [ post, course ] = yield all([
        axios.get("http://localhost:7070/api/posts"),
        axios.get("http://localhost:7070/api/courses"),
      ])

    try {
        console.log(course);
        // yield put(courseActions.getCourses(post));

    } catch (error) {
    }
}

export default function* root() {
    yield all([
        takeEvery(types.GET_POSTS, getPosts)
    ]);
}
